import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import { useState } from 'react';

export default function FormDialog() {
  const [open, setOpen] = React.useState(true);

//   const handleClickOpen = () => {
//     setOpen(true);
//   };

const [empoyeeRole, setemployeeRole] = React.useState('');

  const handleChange = (event) => {
    setemployeeRole(event.target.value);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const initialData = Object.freeze({
    email:"",
    userName:""

  });

  const [formData, updateFormData] = useState(initialData);

  const handleChangeInput = (e) => {
    updateFormData({
      ...formData,

      [e.target.name]:e.target.value.trim()
    });

  }
  const data = formData
  const handleSubmit = (e) => {
    e.preventDefault()
    navigate('/render',{state:{data}})
}

  return (
    <div>
      {/* <Button variant="outlined" onClick={handleClickOpen}>
        Open form dialog
      </Button> */}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Form</DialogTitle>
        <DialogContent>

          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="User Name"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChangeInput}
          />
        </DialogContent>
        <DialogContent>

     <TextField
       autoFocus
       margin="dense"
       id="name"
       label="Email Address"
       type="email"
       fullWidth
       variant="standard"
       onChange={handleChangeInput}
     />
   </DialogContent>
   <InputLabel id="demo-simple-select-helper-label">Employee Role</InputLabel>
   <Select
          labelId="demo-simple-select-helper-label"
          id="demo-simple-select-helper"
          value={empoyeeRole}
          label="employeeRole"
          onChange={handleChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Trainee</MenuItem>
          <MenuItem value={20}>Trainer</MenuItem>
          <MenuItem value={30}>Expert</MenuItem>
        </Select>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit}>Subscribe</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
