const CountAuto = () => {
const[count,setCount] = useState(0);

    const countSub = () => {
        let count = 0

        const countTime = setInterval((previous) => {
           if(previous < 5){
               setCount(count = previous + 1)   
           }
           else if(previous > 0){
            setCount(count = previous - 1)  
            clearInterval(countTime) 
           }
       },1000)

    }



useEffect(() => {
        countSub()     
},[])



return(
<>
<h1>CountAuto is here</h1>
<div>{count}</div>
</>
)
}

