import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import { useState } from 'react';

const ModalPage = () => {

    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
      setOpen(true);
    };

    const initialData = Object.freeze({
        name:"",
        email:""

      })

  const [employeeRole, setemployeeRole] = React.useState(initialData);

    const handleChange = (event) => {
        setemployeeRole(event.target.value);
    };

    const handleClose = () => {
      setOpen(false);
    };

const data = employeeRole
const handleSubmit = (event) => {
   event.preventDefault();
   console.log(`username${event.data}`)
   setOpen(false);
}

    return(
        <>
           <Button variant="outlined" onClick={handleClickOpen}>
        Open form dialog
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Form</DialogTitle>
        <DialogContent>

          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="User Name"
            type="text"
            fullWidth
            variant="standard"
            onChange={handleChange}
          />
        </DialogContent>
        <DialogContent>

     <TextField
       autoFocus
       margin="dense"
       id="email"
       label="Email Address"
       type="email"
       fullWidth
       variant="standard"
       onChange={handleChange}
     />
   </DialogContent>
   <InputLabel id="demo-simple-select-helper-label">Employee Role</InputLabel>
   <Select
          labelId="demo-simple-select-helper-label"
          id="demo-simple-select-helper"
          value={employeeRole}
          label="Role"
          onChange={handleChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Trainee</MenuItem>
          <MenuItem value={20}>Trainer</MenuItem>
          <MenuItem value={30}>Expert</MenuItem>
        </Select>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSubmit}>Subscribe</Button>
        </DialogActions>
      </Dialog>
      <div>
          Form Data 
      </div>
        </>
    )
}

export default ModalPage;
