import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { useLocation,useNavigate } from 'react-router-dom';
import { useState } from 'react';

const LoginPrivatePage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const actualData = location.state.getData;
    const [userData,setUserData] = useState({email: "",password:""});
    const[errorMsg,setErrorMsg] = useState({value:""});

    const handleInputChange = (e) => {
      setUserData((prevState) => {
        return{
          ...prevState,
          [e.target.name]:e.target.value,
        };
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      if(userData.email === "" || userData.password === ""){
        setErrorMsg(() => ({
         value : "empty email/password"
        }));
      }else if(
        userData.email.toLowerCase() === "admin@gmail.com" &&
        userData.password === "123456"
      ){
        localStorage.setItem("isAuthenticated",true);
        window.location.pathname = "/ModalPage";

      }else{
        setErrorMsg(() => ({value:"Invalid email/password"}));
        return;
      }
    };

  const register = (e) => {
    e.preventDefault();
    navigate('/')
  }

    return(
        <>
          <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems:'center',
        '& .MuiTextField-root': { width: '25ch' },
      }}
    >
      <h1>Welcome to Log In Page:</h1>
      <div>{actualData}</div>
      <TextField type="email" label={'Email="email"'} id="margin-normal" margin="normal"  onChange={(e) => handleInputChange(e)}/>
      <TextField type="password" label={'Password="password"'} id="margin-normal" margin="normal" onChange={(e) => handleInputChange(e)}/>
      <Button variant="contained" onClick={(e) => handleSubmit(e)}>Submit</Button>
      <h2>OR</h2>   
      <Button variant="contained" onClick={register}>Register</Button>
    </Box>
        </>
    )
}
export default LoginPrivatePage;
