import { useState , useEffect } from "react";

const CountAuto = () => {
const[count,setCount] = useState(0);

    const countSub = () => {
        let count = 0
       setInterval(() => {
           if(count < 20){
               setCount(count = count + 1)
           }else if(count == 20){
                setCount(count = count - 1)  
           }

       },1000)
    }



useEffect(() => {
        countSub()     
},[])



return(
<>
<h1>CountAuto is here</h1>
<div>{count}</div>
</>
)
}

export default CountAuto;
