import { useState , useEffect } from "react";

const CountAuto = () => {
const[count,setCount] = useState(0);

useEffect(() =>{
    const Subscribe = () => {
        setTimeout(() => {
            setCount((count) => count + 1)
        },1000) 
        if(count > 20){
                setTimeout(() => {
                setCount((count) => count = 0)
                }) 
            }
    }

})

useEffect(() => {
    Subscribe()
},[])

return(
<>
<h1>CountAuto is here</h1>
<div>{count}</div>
</>
)
}

export default CountAuto;
